import React, { createContext, useContext, useState, useEffect, ReactNode } from 'react';
import { Session, User } from '@supabase/supabase-js';
import { supabase } from '../services/supabase';
import { createCompleteUser, getCompleteUserByEmail } from '../services/crudService';

interface AuthContextType {
  session: Session | null;
  user: User | null;
  loading: boolean;
  signIn: (email: string, password: string) => Promise<{ error: any }>;
  signUp: (email: string, password: string, username: string, adminPassword: string) => Promise<{ error: any }>;
  signOut: () => Promise<void>;
}

const AuthContext = createContext<AuthContextType | undefined>(undefined);

interface AuthProviderProps {
  children: ReactNode;
}

export const AuthProvider: React.FC<AuthProviderProps> = ({ children }) => {
  const [session, setSession] = useState<Session | null>(null);
  const [user, setUser] = useState<User | null>(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Obter sessão atual
    supabase.auth.getSession().then(({ data: { session } }) => {
      setSession(session);
      setUser(session?.user ?? null);
      setLoading(false);
    });

    // Configurar listener para mudanças na autenticação
    const { data: { subscription } } = supabase.auth.onAuthStateChange((_event, session) => {
      setSession(session);
      setUser(session?.user ?? null);
      setLoading(false);
    });

    return () => subscription.unsubscribe();
  }, []);

  const signIn = async (email: string, password: string) => {
    try {
      const { error } = await supabase.auth.signInWithPassword({ email, password });
      return { error };
    } catch (error) {
      return { error };
    }
  };

  const signUp = async (email: string, password: string, username: string, adminPassword: string) => {
    // Verificar se todos os campos foram preenchidos
    if (!email || !password || !username || !adminPassword) {
      return { error: { message: 'Todos os campos são obrigatórios' } };
    }

    // Verificar senha de administrador usando a variável de ambiente
    const ADMIN_PASSWORD = process.env.REACT_APP_ADMIN_PASSWORD;

    if (adminPassword !== ADMIN_PASSWORD) {
      return { error: { message: 'Senha de autorização incorreta!' } };
    }

    // Validar formato de email
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(email)) {
      return { error: { message: 'Formato de email inválido' } };
    }

    // Validar tamanho da senha
    if (password.length < 6) {
      return { error: { message: 'A senha deve ter pelo menos 6 caracteres' } };
    }

    // Validar nome de usuário
    if (username.length < 3) {
      return { error: { message: 'O nome de usuário deve ter pelo menos 3 caracteres' } };
    }

    try {
      // 1. Registrar o usuário no sistema de autenticação do Supabase
      const { data: authData, error: authError } = await supabase.auth.signUp({
        email,
        password,
        options: {
          data: { username }
        }
      });

      if (authError) {
        return { error: authError };
      }

      // 2. Criar o usuário completo (empresa + restrição) usando o serviço CRUD
      try {
        await createCompleteUser(username, email, password, 'admin');
        return { error: null };
      } catch (crudError) {
        console.error('Erro ao criar usuário usando o serviço CRUD:', crudError);
        return { error: { message: 'Erro ao criar perfil de usuário' } };
      }
    } catch (error) {
      console.error('Erro no processo de cadastro:', error);
      return { error };
    }
  };

  const signOut = async () => {
    await supabase.auth.signOut();
  };

  const value = {
    session,
    user,
    loading,
    signIn,
    signUp,
    signOut
  };

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
};

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (context === undefined) {
    throw new Error('useAuth deve ser usado dentro de um AuthProvider');
  }
  return context;
};

export default AuthContext;
