import styled from 'styled-components';

export const LogoContainer = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  margin-bottom: 30px;
  width: 100%;
`;

export const LogoWrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 2px;
`;

export const LogoImage = styled.img`
  height: 60px;
  width: auto;
  filter: brightness(0) saturate(100%) invert(100%) sepia(100%) saturate(1000%) hue-rotate(100deg) brightness(100%) contrast(100%);
  /* Aplicando a mesma cor do texto (#00ff41) */
`;

export const LogoText = styled.h1`
  font-family: 'MuseoModerno', cursive;
  font-size: 28px;
  font-weight: 700;
  color: var(--text-color); /* Verde terminal clássico #00ff41 */
  letter-spacing: 1px;
  text-transform: lowercase;
  text-shadow: 0 0 5px var(--text-color);
`;
