import React, { createContext, useContext, useState, useEffect, ReactNode } from 'react';
import { Session, User } from '@supabase/supabase-js';
import { supabase } from '../services/supabase';

interface AuthContextType {
  session: Session | null;
  user: User | null;
  loading: boolean;
  signIn: (email: string, password: string) => Promise<{ error: any }>;
  signUp: (email: string, password: string, username: string, adminPassword: string) => Promise<{ error: any }>;
  signOut: () => Promise<void>;
}

const AuthContext = createContext<AuthContextType | undefined>(undefined);

interface AuthProviderProps {
  children: ReactNode;
}

export const AuthProvider: React.FC<AuthProviderProps> = ({ children }) => {
  const [session, setSession] = useState<Session | null>(null);
  const [user, setUser] = useState<User | null>(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Obter sessão atual
    supabase.auth.getSession().then(({ data: { session } }) => {
      setSession(session);
      setUser(session?.user ?? null);
      setLoading(false);
    });

    // Configurar listener para mudanças na autenticação
    const { data: { subscription } } = supabase.auth.onAuthStateChange((_event, session) => {
      setSession(session);
      setUser(session?.user ?? null);
      setLoading(false);
    });

    return () => subscription.unsubscribe();
  }, []);

  const signIn = async (email: string, password: string) => {
    try {
      const { error } = await supabase.auth.signInWithPassword({ email, password });
      return { error };
    } catch (error) {
      return { error };
    }
  };

  const signUp = async (email: string, password: string, username: string, adminPassword: string) => {
    // Verificar senha de administrador - usando uma senha fixa para demonstração
    // Em produção, seria melhor usar variáveis de ambiente ou um sistema mais seguro
    const ADMIN_PASSWORD = 'vale-sis-admin';

    if (adminPassword !== ADMIN_PASSWORD) {
      return { error: { message: 'Senha de autorização incorreta!' } };
    }

    // Validar email e senha
    if (!email || !password || !username) {
      return { error: { message: 'Email, senha e nome de usuário são obrigatórios' } };
    }

    if (password.length < 6) {
      return { error: { message: 'A senha deve ter pelo menos 6 caracteres' } };
    }

    try {
      // 1. Registrar o usuário no sistema de autenticação do Supabase
      const { data: authData, error: authError } = await supabase.auth.signUp({
        email,
        password,
        options: {
          data: { username }
        }
      });

      if (authError) {
        return { error: authError };
      }

      // 2. Inserir um registro na tabela dv_restricao_user (padrão como 'admin')
      const { data: restricaoData, error: restricaoError } = await supabase
        .from('dv_restricao_user')
        .insert([{ dv_tipo_restricao: 'admin' }])
        .select();

      if (restricaoError) {
        console.error('Erro ao criar registro de restrição:', restricaoError);
        return { error: { message: 'Erro ao criar perfil de usuário' } };
      }

      // 3. Inserir um registro na tabela dv_cad_empresas_drive
      const restricaoId = restricaoData[0].id;
      const { error: empresaError } = await supabase
        .from('dv_cad_empresas_drive')
        .insert([{
          dv_nome: username,
          dv_email: email,
          dv_senha: password, // Nota: em produção, não armazenar senhas em texto simples
          dv_tipo_restricao: restricaoId
        }]);

      if (empresaError) {
        console.error('Erro ao criar registro de empresa:', empresaError);
        return { error: { message: 'Erro ao criar perfil de empresa' } };
      }

      return { error: null };
    } catch (error) {
      console.error('Erro no processo de cadastro:', error);
      return { error };
    }
  };

  const signOut = async () => {
    await supabase.auth.signOut();
  };

  const value = {
    session,
    user,
    loading,
    signIn,
    signUp,
    signOut
  };

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
};

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (context === undefined) {
    throw new Error('useAuth deve ser usado dentro de um AuthProvider');
  }
  return context;
};

export default AuthContext;
