import React, { useEffect } from 'react';
import { Container, ToastContainer, StatusIcon, Message, CloseButton } from './styles';

export type ToastType = 'success' | 'error';

interface ToastProps {
  message: string;
  type?: ToastType;
  visible: boolean;
  onClose: () => void;
}

const Toast: React.FC<ToastProps> = ({ message, type = 'success', visible, onClose }) => {
  useEffect(() => {
    // Esconder o toast automaticamente apenas para mensagens de sucesso
    if (visible && type === 'success') {
      const timer = setTimeout(() => {
        onClose();
      }, 3000);

      return () => clearTimeout(timer);
    }
  }, [visible, type, onClose]);

  if (!visible) return null;

  return (
    <Container className={`${type} ${visible ? 'show' : ''}`}>
      <ToastContainer>
        <StatusIcon>
          [ <span>STATUS</span> ]
        </StatusIcon>
        <Message>{message}</Message>
        {type === 'error' && (
          <CloseButton onClick={onClose}>&times;</CloseButton>
        )}
      </ToastContainer>
    </Container>
  );
};

export default Toast;
